import React, {
  useState,
  useEffect,
  useReducer,
  useLayoutEffect,
  useContext,
  useRef,
  memo,
  useMemo,
  useCallback
} from "react";

function countReducer(state, action) {
  switch (action.type) {
    case "add":
      return state + 1;
    case "minus":
      return state - 1;
    default:
      return state;
  }
}

function MyCountFunc() {
  //   const [count, setCount] = useState(0);
  const [count, dispatchCount] = useReducer(countReducer, 0);
  const [name, setName] = useState("lyn");
  //   const countRef = useRef();
  //   countRef.current = count;
  //   const handleAlertButtonClick = () => {
  //     setTimeout(() => {
  //       // alert(count)
  //       alert(countRef.current);
  //     }, 2000);
  //   };
  //   useEffect(() => {
  //     const interval = setInterval(() => {
  //       // 拿到的值永远是最新的值，避免闭包陷阱
  //       //   setCount(c => c + 1);
  //       dispatchCount({ type: "minus" });
  //     }, 1000);
  //     return () => clearInterval(interval);
  //   }, []);
  useEffect(() => {
    // 每次有渲染，就先执行return，再执行回调
    console.log("effect invokeed");
    return () => console.log(11111111);
  }, [count]);
  return (
    <div>
      <input value={name} onChange={e => setName(e.target.value)} />
      <span>{count}</span>
      {/* <Child config={config} onButtonClick={handleButtonClick}></Child> */}
      <button onClick={() => dispatchCount({ type: "minus" })}>{count}</button>
    </div>
  );
}
export default MyCountFunc;
class MyCount extends React.Component {
  state = {
    count: 0
  };
  componentDidMount() {
    this.interval = setInterval(() => {
      this.setState({ count: this.state.count + 1 });
    }, 1000);
  }
  componentWillUnmount() {
    this.interval ? clearInterval(this.interval) : null;
  }
  render() {
    return <span>{count}</span>;
  }
}
